import "./sign-up-form.styles.scss";
import { useState } from "react";
import { createAuthUserWithEmailAndPassword } from "../../utils/firebase/firebase.utils";

const defaultFormFields = {
  displayName: "",
  email: "",
  password: "",
  confirmPassWord: "",
};

const SignUpForm = () => {
  const [formField, setFormField] = useState(defaultFormFields);
  const {displayName, email, password, confirmPassWord} = formField
  const [checkPassword, setCheckPassword] = useState({
    message: "",
    color: "black",
  });

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormField({ ...formField, [name]: value });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (formField.password !== formField.confirmPassWord) {
      const isPasswordTheSame = { ...checkPassword };
      isPasswordTheSame.message = "password does not match";
      isPasswordTheSame.color = "red";
      return setCheckPassword({
        ...checkPassword,
        ["message"]: "password does not match",
      });
    }
    setCheckPassword("");
    try {
        const response = await createAuthUserWithEmailAndPassword(
            email,
            password
          );
          console.log(response);
    } catch (error) {
        console.log("user creation encounter an err", error);
    }
   // console.log(newUser);
  };
  return (
    <div>
      <h1> sign up with your email and password</h1>
      <form className="sing-up-form" onSubmit={handleSubmit}>
        <label htmlFor="displayName"> Display Name</label>
        <input
          required
          value={formField.displayName}
          type="text"
          id="displayName"
          name="displayName"
          onChange={handleChange}
        />
        <label htmlFor="email">Email</label>
        <input
          required
          value={formField.email}
          type="email"
          id="email"
          name="email"
          onChange={handleChange(event)}
        />
        <label htmlFor="Password"> Password</label>
        <input
          style={{ borderColor: checkPassword.color }}
          required
          value={formField.password}
          type="password"
          id="password"
          name="password"
          onChange={(event) => handleChange(event)}
        />
        <label htmlFor="confirmPassword"> Confirm Password</label>
        <input
          style={{ borderColor: checkPassword.color }}
          required
          value={formField.confirmPassWord}
          type="password"
          id="confirmPassword"
          name="confirmPassWord"
          onChange={(event) => handleChange(event)}
        />
        <span>{checkPassword.message}</span>
        <button type="submit"> SIGN UP</button>
      </form>
    </div>
  );
};

export default SignUpForm;
