import "./cart-item.styles.scss";

const CartItem = ({ item }) => {
  const { imageUrl, name, quantity, price } = item;

  return (
    <div className="cart-item-container">
      <img src={imageUrl} alt={name} />
      <div>
        <span>{name }</span>
        <span className="price">{`${name} ${quantity} x $${price}`}</span>{" "}
      </div>
    </div>
  );
};

export default CartItem;
