import { useState, useContext, LinK} from "react"
import { LoginContext } from "../context/login.context"

const LogIn = () => {
    const [logIn, setLogIn] = useState("")
    const [,setContextLogin] = useContext(LoginContext)
    const handleChange = (e) => {
        setLogIn(e.currentTarget.value)
    }

    const handleSubmit = (e)=>{
        e.preventDefault()
        setContextLogin(logIn)
    }

    console.log(logIn);
    return <div> <input value={logIn} type="text" onChange={(event)=> handleChange(event)}/> <button onClick={(e)=> handleSubmit(e)} > Login</button> </div>
}


export default LogIn
