import "./sign-up-form.styles.scss";
import { useState } from "react";
import { createAuthUserWithEmailAndPassword } from "../../utils/firebase/firebase.utils";
import { async } from "@firebase/util";

const defaultFormFields = {
  displayName: "",
  email: "",
  password: "",
  confirmPassWord: "",
};

const SignUpForm = () => {
  const [formField, setFormField] = useState(defaultFormFields);
  const [checkPassword, setCheckPassword] = useState({message: "", colo});

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormField({ ...formField, [name]: value });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (formField.password !== formField.confirmPassWord)
      return setCheckPassword("password does not match");

    setCheckPassword("")  
    // await createAuthUserWithEmailAndPassword(
    //   formField.email,
    //   formField.password
    // );
  };
  return (
    <div>
      <h1> sign up with your email and password</h1>
      <form className="sing-up-form" onSubmit={(event) => handleSubmit(event)}>
        <label htmlFor="displayName"> Display Name</label>
        <input
          required
          value={formField.displayName}
          type="text"
          id="displayName"
          name="displayName"
          onChange={(event) => handleChange(event)}
        />
        <label htmlFor="email">Email</label>
        <input
          required
          value={formField.email}
          type="email"
          id="email"
          name="email"
          onChange={(event) => handleChange(event)}
        />
        <label htmlFor="Password"> Password</label>
        <input
          required
          value={formField.password}
          type="password"
          id="password"
          name="password"
          onChange={(event) => handleChange(event)}
        />
        <label htmlFor="confirmPassword"> Confirm Password</label>
        <input
          required
          value={formField.confirmPassWord}
          type="password"
          id="confirmPassword"
          name="confirmPassWord"
          onChange={(event) => handleChange(event)}
        />
        <span>{checkPassword}</span>
        <button type="submit"> SIGN UP</button>
      </form>
    </div>
  );
};

export default SignUpForm;
