import { useContext } from "react";
import { CartContext } from "../../context/cart.context";
import CartItem from "../cart-item/cart-item.component";

import CheckoutItems from "../checkout-items/checkout-items.component";

const Checkout = () => {
  const { cartItems, addItemToCart, removeItemFromCart, deleteItemFromCart } =
    useContext(CartContext);
  console.log(cartItems);
  return (
    <div className="checkout-container">
      <div className="checkout-header">
        <div className="header"></div>
        {cartItems.map((item) => {
          const { id, name, quantity, imageUrl } = item;
          return (
            <div key={id}>
              <h2>{name}</h2>
              <img src={imageUrl} alt="" />

              <span>{quantity}</span>
              <br />
              <span onClick={() => removeItemFromCart(item)}>decrement</span>
              <br />
              <span onClick={() => addItemToCart(item)}>increment</span>
              <br />
              <span onClick={() => deleteItemFromCart(item)}>X</span>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Checkout;

