import "./directory-item.styles.scss";
import { Link } from "react-router-dom";

const DirectoryItem = ({imageUrl, title}) => {
  return (
    <Link><div >
      <div
        className="background-image"
        style={{ backgroundImage: `url(${imageUrl})` }}
      />
      <div className="body">
        <h2>{title}</h2>
        <p>Shop Now</p>
      </div>
    </div> </Link>
  );
};


export default DirectoryItem
