import { useContext } from "react";
import { CartContext } from "../../context/cart.context";
import CartItem from "../cart-item/cart-item.component";

import CheckoutItems from "../checkout-items/checkout-items.component";

const Checkout = () => {
  const { cartItems, addItemToCart, removeItemFromCart } = useContext(CartContext);
  console.log(cartItems);
  return (
    <div>
      <div>
        {cartItems.map((item) => {
          const { id, name, quantity } = item;
          return (
            <div key={id}>
              <h2>{name}</h2> 

              <span>{quantity}</span>
              <br/>
              <span onClick={() => removeItemFromCart(item)}>decrement</span>
              <br />
              <span onClick={() => addItemToCart(item)}>increment</span>
              <br />
              <span onClick={()=> deleteItemFromCart}>X</span>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Checkout;

