import {
  signInWithGooglePopup,
  createUserDocumentFromAuth,
  signInAuthUserWithEmailAndPassword,
} from "../../utils/firebase/firebase.utils";

import { useState } from "react";
import Button from "../button/button.component";
import FormInput from "../form-input/form-input.component";

import "./sign-in-form.styles.scss";

const SignInDefaultFields = { email: "", password: "" };
const SignInForm = () => {
  const logGoogleUser = async () => {
    const { user } = await signInWithGooglePopup();
    await createUserDocumentFromAuth(user);
  };

  const [signIn, setSignIn] = useState(SignInDefaultFields);
  const { email, password } = signIn;

  const handleChange = (e) => {
    const { name, value } = e.target;
    setSignIn({ ...signIn, [name]: value });
  };

  const restFormFields = () => {
    setSignIn(SignInDefaultFields);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      console.log(email, password);
      const response = await signInAuthUserWithEmailAndPassword(
        email,
        password
      );
      console.log(response);
      restFormFields();
    } catch (error) {
      console.log(error);
    }
  };
  return (
    <div className="sing-up-container">
      <h1>I already have an account</h1>
      <p>Sign in with your email and password</p>
      <form className="sing-up-form" onSubmit={handleSubmit}>

        <FormInput
          required
          value={formField.email}
          type="email"
          label="Email"
          name="email"
          onChange={handleChange}
        />

        <FormInput
          style={{ borderColor: checkPassword.color }}
          required
          value={formField.password}
          type="password"
          label="Password"
          name="password"
          onChange={handleChange}
        />

        {/* button not showing on screen */}
        <Button type="submit"> SIGN UP</Button> 
      </form>
    </div>
  );
};
